import React, { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = (props) => {
  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEntredDate] = useState("");

  const titleChangeHandleer = (event) => {
    setEnteredTitle(event.target.value);
  };

  const amountChangeHandler = (event) => {
    setEnteredAmount(event.target.value);
  };

  const dateChangeHadler = (event) => {
    setEntredDate(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const expenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate),
    };

    props.onSaveExpenseData(expenseData);
    setEnteredTitle("");
    setEnteredAmount("");
    setEntredDate("");
  };

  const handleAddExpenseButton = function () {
    const [buttonVal, setButtonVal] = useState(false);

    if (buttonVal === false) {
      return (
        <div className="new-expense__actions">
          <button type="submit" onClick={handleAddExpenseButton}>
            Add Expense
          </button>
        </div>
      );
    }
  };

  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input
            type="text"
            value={enteredTitle}
            onChange={titleChangeHandleer}
          />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            type="text"
            value={enteredAmount}
            onChange={amountChangeHandler}
          />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            type="date"
            value={enteredDate}
            min={"2019-01-01"}
            max={"2022-12-31"}
            onChange={dateChangeHadler}
          />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="cancel">Cancel</button>
        <button type="submit" onClick={handleAddExpenseButton}>
          Add Expense
        </button>
      </div>
    </form>
  );
};

export default ExpenseForm;
